    if(rev.count(nxt)==0)fail();
    par[id] = rev[nxt];
    children[par[id]].push_back(id);
    cnt[par[id]] += cnt[id];
  }

  int root = 0;
  REP(i,n)if(par[i]==-1)root=i;

