inline void solve()
{
	static priority_queue<node> Q;
	static unordered_map<LL, int> id;
	static int size[MAXN + 5], vis[MAXN + 5];

	for(int i = 1; i <= n; ++i)
	{
		Q.push(d[i]);
		id[d[i].D] = d[i].u;
		size[d[i].u] = 1;
	}

	static vector<pii> E;

	while(SZ(Q) > 1)
	{
		int u = Q.top().u;
		LL D = Q.top().D;
		vis[u] = 1, Q.pop();

		int v = id[D + 2 * size[u] - n];
		if(!v || vis[v]) { puts("-1"); return; }
		E.emplace_back(u, v), size[v] += size[u];
	}
	for(auto i : E) printf("%d %d\n", i.fst, i.snd);
}

