            return 0;
        }
        fa[now] = M[f];
        sz[fa[now]] += sz[now];
        ::addedge2(now, fa[now]);
    }
    int sum = 0;
    memset(vis, 0, sizeof(vis));
    dfs(1, sum);
    if(sum != n) {
        puts("-1");
        return 0;
    }
    for(int i = 1; i <= n; ++i) if(fa[i] != 0) {
        std::cout << fa[i] << ' ' << i << '\n';
    }
    return 0;
}

