void dfs(int u = 1, int p = -1)
{
  sz[u] = 1;
  f[1] += depth[u];
  for(int v : adj[u]) if(v != p){
    depth[v] = depth[u] + 1;
    dfs(v, u);
    sz[u] += sz[v];
  }
}

void reroot(int u = 1, int p = -1)
{
  for(int v : adj[u]) if(v != p){
    f[v] = f[u] + N - 2 * sz[v];
    reroot(v, u);
  }
}

bool check(void)
{
  for(int i = 1; i <= N; ++i)
    if(par[i]) adj[par[i]].pb(i), adj[i].pb(par[i]);
  dfs(); reroot();
  for(int i = 1; i <= N; ++i){
    if(f[d[i].se] != d[i].fi) return false;
  }
  return true;
}

