    }
    for(auto& k : M) if(k.first != (*M.rbegin()).first) {
        int now = k.second;
        ++sz[now];
        ll f = k.first + 2ll * sz[now] - n;
        if(M.count(f) == 0) {
            puts("-1");
            return 0;
        }
        fa[now] = M[f];
        sz[fa[now]] += sz[now];
        ::addedge2(now, fa[now]);
    }
