int check(vector<vector<int> > G, int root,int d){

  function<int(int,int,int)>  dfs = [&](int pos,int pre,int dis){
    int res = 0;
    for(int to:G[pos]){
      if(to == pre) continue;
      res += dfs(to, pos, dis+1);
    }
    return res;
  };
  return dfs(root, -1, 0) == d;
}

