struct Node {
	int parent{-1}, children{1}, id{};
	long long d, distance_sum{};
};

int main()
{
	int n;
	scanf("%d", &n);
	std::vector<Node> nodes(n);
	for (int i = 0; i < n; i++)
	{
		scanf("%lld", &nodes[i].d);
		nodes[i].id = i;
	}
	std::sort(nodes.begin(), nodes.end(),
		[](const Node& a, const Node& b){ return a.d < b.d; }
	);
	for (auto node{nodes.rbegin()}; node + 1 < nodes.rend(); node++)
	{
		auto parent = std::lower_bound(nodes.begin(), nodes.end(), node->d - n + 2 * node->children,
			[](Node &e, long long value){ return e.d < value; }
		);
		if (parent == nodes.end() || parent->d != node->d - n + 2 * node->children || parent->id == node->id)
		{
			printf("-1\n");
			return 0;
		}
		node->parent = parent->id;
		parent->children += node->children;
		parent->distance_sum += node->distance_sum + node->children;
	}
	if (nodes[0].distance_sum != nodes[0].d)
	{
		printf("-1\n");
		return 0;
	}
	for (auto node{nodes.begin() + 1}; node < nodes.end(); node++)
	{
		printf("%d %d\n", node->id + 1, node->parent + 1);
	}
	return 0;
}

