using namespace std;

const int INF = 1e9;

struct DSU {
    vector<int> parent, set_size;
    set<int> roots;

    DSU () {}

    DSU(int n) {
        assign(n);
    }

    void assign(int n) {
        parent.resize(n);
        set_size.assign(n, 1);
        iota(parent.begin(), parent.end(), 0);
        roots.insert(parent.begin(), parent.end());
    }

    bool check(int a, int b) {
        return root(a) == root(b);
    }

    int root(int x) {
        while (x != parent[x]) {
            x = parent[x] = parent[parent[x]];
        }
        return x;
    }

    int size() { return roots.size(); }

    int size(int i) { return set_size[root(i)]; }

    bool join(int a, int b) {
        a = root(a), b = root(b);

        if (a == b) {
            return false;
        }

        if (set_size[a] < set_size[b]) {
            swap(a, b);
        }

        roots.erase(b);
        set_size[a] += set_size[b];
        parent[b] = a;
        return true;
    }
};


int main() {
    ios_base::sync_with_stdio(0);

    int n;
    cin >> n;
    vector<pair<int, int> > v;
    for (int i = 0, d; i < n; i++) {
        cin >> d;
        v.emplace_back(d, i);
    }
    sort(v.begin(), v.end());
    vector<pair<int, int> > edges;
    DSU d(n);
    bool res = true;
    for (auto it = v.end() - 1; res and it != v.begin(); it--) {
        const int u = it->second;
        const int next_d = it->first - (n - 2 * d.size(u));
        const auto p = make_pair(next_d, 0);
        auto i = lower_bound(v.begin(), it, p);
        if (i == it or i->first != next_d) {
            res = false;
        }
        else {
            d.join(u, i->second);
            edges.emplace_back(u, i->second);
        }
    }
    if (res) {
        for (auto p : edges) {
            cout << p.first + 1 << ' '
                 << p.second + 1 << '\n';
        }
    } 
    else {
        cout << "-1\n";
    }
}

