void insert(long long x,int id)
{
	long long i=x%mo;
	while (hs[i]!=0)
	{
		i++;
		if (i==mo) i=0;
	}
	hs[i]=id;
}

int find(long long x)
{
	long long i=x%mo;
	while (hs[i]!=0 && d[hs[i]]!=x)
	{
		i++;
		if (i==mo) i=0;
	}
	return hs[i];
}

void qsort(int l,int r)
{
	int i=l;int j=r;
	long long m=d[(i+j)/2];
	while (i<=j)
	{
		while (d[i]>m) i++;
		while (d[j]<m) j--;
		if (i<=j)
		{
			swap(d[i],d[j]);
			swap(id[i],id[j]);
			i++;j--;
		}
	}
	if (l<j) qsort(l,j);
	if (r>i) qsort(i,r); 
}

