using namespace std;
using ll = long long;
const int mod = 1e9+7;

vector<P> ans;

int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(nullptr);

  int n; cin >> n;
  vector<ll> vec(n);
  unordered_map<ll, int> mp;

  rep(i, n) {
    cin >> vec.at(i);
    mp[vec.at(i)] = i+1;
  }

  vector<int> used(n);
  rep(i, n) {
    used.at(i) = 1;
  }
  vector<int> graph[n+1];

  sort(vec.rbegin(), vec.rend());
  rep(i, n) {
    ll a = vec.at(i);
    int s = mp[a];
    int d = n-(2*used.at(s-1));

    if (d > 0 && mp[a-d] > 0) {
      int t = mp[a-d];
      used.at(t-1) += used.at(s-1);
      ans.push_back(P(s,t));
      graph[s].push_back(t);
      graph[t].push_back(s);
    }
  }

  vector<int> distance(n);
  rep(i, n) {
    distance.at(i) = INT_MAX;
  }
  queue<P> que;
  que.push(P(0, 1));
  while (!que.empty()) {
    P pa = que.front(); que.pop();
    int pos = pa.second;
    int d = pa.first;
    distance.at(pos-1) = d;
    for(auto e : graph[pos]) {
      if (distance.at(e-1) == INT_MAX) {
        que.push(P(d+1, e));
      }
    }
  }

  ll sum = 0;
  rep(i, n) {
    if (distance.at(i) == INT_MAX) {
      cout << -1 << endl;
      return 0;
    }
    sum += distance.at(i);
  }
  if (mp[sum] != 1) {
    cout << -1 << endl;
    return 0;
  }

  for(auto e: ans) {
    cout << e.first << ' ' << e.second << '\n';
  }
}

