  vector<vector<int>> children;
  vector<int> depth;

  Tree() {}

  Tree(int n) : children(n), depth(n, -1) {
    for (int i = 0; i < n; ++i) parent.emplace_back(i);
  }

  int size() const {
    return parent.size();
  }

  template<typename... Args> void addEdge(int from, int to, Args... args) {
    parent[from] = Edge(to, args...);
    children[to].emplace_back(from);
  }

  void addEdge(int from, const Edge& edge) {
    parent[from] = edge;
    children[edge.to].emplace_back(from);
  }

  vector<Edge> getEdges(int from) const {
    vector<Edge> res;
    for (int v : children[from]) {
      auto e = parent[v];
      e.to = v;
      res.emplace_back(e);
    }
    if (from != parent[from].to) res.emplace_back(parent[from]);
    return res;
  }

  int getDepth(int v) {
    if (depth[v] != -1) return depth[v];
    if (parent[v].to == v) return depth[v] = 0;
    return depth[v] = getDepth(parent[v].to) + 1;
  }

  vector<int> getPath(int v) {
    vector<int> res{v};
    while (v != parent[v].to) {
      v = parent[v].to;
      res.emplace_back(v);
    }
    return res;
  }
};

template<typename Edge> struct BFSState {
  int pos, prv;

  BFSState(const int pos, const int prv = -1) : pos(pos), prv(prv) {}

  BFSState next(const int pos, const Edge& edge) const {return BFSState(edge.to, pos);}

  int getPos() {return pos;}
};

template<typename Graph, typename State = BFSState<typename Graph::EdgeType>> class BFS : public Search<Graph, State> {
protected:
  using Edge = typename Graph::EdgeType;

  queue<State> que;

  void push(const State& state) {que.push(state);}

  State next() {
    State now = que.front();
    que.pop();
    return now;
  }

  bool isRunning() {return !que.empty();}

public:
  BFS(const Graph& graph) : Search<Graph, State>(graph) {}
};

template<typename Graph, bool Restoration = false> class BFSDistance : public BFS<Graph> {
private:
  using Edge = typename Graph::EdgeType;
  using State = BFSState<typename Graph::EdgeType>;

  void visit(const State& state) {
    if (state.prv != -1) {
      if (Restoration) shortestPathTree.addEdge(state.pos, state.prv);
      dis[state.pos] = dis[state.prv] + 1;
    }
  }

public:
  vector<int> dis;
  Tree<Edge> shortestPathTree;

  BFSDistance(const Graph& graph) : BFS<Graph>(graph), dis(graph.size(), 0) {
    if (Restoration) shortestPathTree = Tree<Edge>(graph.size());
  }
};

template<typename Graph> BFSDistance<Graph> bfsDistance(const Graph& graph, int from) {
  BFSDistance<Graph> bfs(graph);
  bfs.solve(from);
  return bfs;
}

template<typename Graph> typename Graph::EdgeType::CostType bfsDistance(const Graph& graph, int from, int to) {
  return bfsDistance(graph, from).dis[to];
}

template<typename Graph> BFSDistance<Graph, true> bfsDistanceTree(const Graph& graph, int from) {
  BFSDistance<Graph, true> bfs(graph);
  bfs.solve(from);
  return bfs;
}

template<typename Edge> struct FullEdge : public Edge {
  int from;

  FullEdge() = default;

  FullEdge(const int from, const Edge& edge) : Edge(edge), from(from) {}
};

struct Edge {
  using CostType = int;
  const static int cost = 1;
  int to;
  Edge(int to = -1) : to(to) {}
  bool isNone() const {return to == -1;}
};

istream& operator>>(istream& s, FullEdge<Edge>& edge) {
  s >> edge.from >> edge.to;
  return s;
}

ostream& operator<<(ostream& s, const FullEdge<Edge>& edge) {
  s << '(' << edge.from << ',' << edge.to << ')';
  return s;
}

template<typename Cost> struct WeightedEdge : public Edge {
  using CostType = Cost;
  Cost cost;
  WeightedEdge(int to = -1, Cost cost = 0) : Edge(to), cost(cost) {}
};

template<typename Cost> istream& operator>>(istream& s, FullEdge<WeightedEdge<Cost>>& edge) {
  s >> edge.from >> edge.to >> edge.cost;
  return s;
}

template<typename Cost> ostream& operator<<(ostream& s, const FullEdge<WeightedEdge<Cost>>& edge) {
  s << '(' << edge.from << ',' << edge.to << ',' << edge.cost << ')';
  return s;
}

template<typename Capacity> struct ResidualEdge : public Edge {
  using CapacityType = Capacity;
  Capacity cap;
  int rev;
  ResidualEdge(int to = -1, Capacity cap = 0) : Edge(to), cap(cap) {}
  ResidualEdge reverse(int from) const {return ResidualEdge(from, 0);}
};

template<typename Capacity, typename Cost> struct WeightedResidualEdge : public ResidualEdge<Capacity> {
  Cost cost;
  WeightedResidualEdge(int to = -1, Capacity cap = 0, Cost cost = 0) : ResidualEdge<Capacity>(to, cap), cost(cost) {}
  WeightedResidualEdge reverse(int from) const {return WeightedResidualEdge(from, 0, -cost);}
};

