    for(int i=0; i<N; i++)
        d2idx[D[i].first] = i;

    vector<long long> E(N, 1);
    vector<pair<int, int>> edges;
    for(int i=0; i<N-1; i++){
        long long d0 = D[i].first;
        long long d1 = D[i].first + 2 * E[i] - N;
