int const nmax = 100000;
ll dist[1 + nmax];
int ind[1 + nmax], sz[1 + nmax];
bool compare(int x, int y){
  return dist[x] < dist[y];
}

vector<int> g[1 + nmax];
int level[1 + nmax];

ll dfs(int node){
  ll result = level[node];
  for(int h = 0; h < g[node].size(); h++){
    int to = g[node][h];
    level[to] = level[node] + 1;
    result += dfs(to);
  }
  return result;
}

int main()
{
  int n;
  cin >> n;
  map<ll,int> code;
  for(int i = 1; i <= n; i++)
