  for(int i = 1; i <= n; i++) {
    ind[i] = i;
    sz[i] = 1;
    code[dist[i]] = i;
  }
  sort(ind + 1, ind + n + 1, compare);

  for(int i = n; 1 < i; i--){
    int node = ind[i];
    int far = code[dist[node] + sz[node] - (n - sz[node])];
    if(far == 0){
      cout << "-1";
      return 0;
    } else {
      g[far].push_back(node);
      sz[far] += sz[node];
    }
  }
  ll result = dfs(ind[1]);
  if(dist[ind[1]] == result){
    for(int i = 1;i <= n; i++)
      for(int h = 0; h < g[i].size(); h++)
        cout << i << " " << g[i][h] << '\n';
  } else
    cout << -1;
  return 0;
}

