typedef long long LL;
typedef pair<int, int> pii;

const int MAXN = (int) 1e5;

int n;

struct node
{
	int u;
	LL D;

	inline bool operator < (const node &rhs) const { return this->D < rhs.D; }

	node() { }
	node(int _u, LL _D): u(_u), D(_D) { }
}d[MAXN + 5];

inline void input()
{
	n = read<int>();
	for(int i = 1; i <= n; ++i) d[i] = node(i, read<LL>());
}

inline void solve()
{
	static priority_queue<node> Q;
	static unordered_map<LL, int> id;
	static int size[MAXN + 5];

	for(int i = 1; i <= n; ++i)
	{
		Q.push(d[i]);
		id[d[i].D] = d[i].u;
		size[d[i].u] = 1;
	}

	static vector<pii> E;

	while(SZ(Q) > 1)
	{
		int u = Q.top().u, D = Q.top().D;
		Q.pop();

		int v = id[D + 2 * size[u] - n];
		if(!v || v == u) { puts("-1"); return; }
		E.emplace_back(u, v), size[v] += size[u];
	}
	for(auto i : E) printf("%d %d\n", i.fst, i.snd);
}

