priority_queue<pll, vector<pll>, greater<pll> > H[2];
int n;
ll d[MAXN], size[MAXN];

int main()
{

	scanf("%d", &n);
	for (int i = 1; i <= n; ++ i)
	{
		scanf("%lld", &d[i]);
		H[d[i]%2].emplace(d[i], i);
	}
	d[0] = 1e13;
	int r1 = 0, r2 = 0;
	if (!H[0].empty()) r1 = H[0].top().Y;
	if (!H[1].empty()) r2 = H[1].top().Y;
	int y = (d[r1] < d[r2]) ? r1 : r2;
	if (y == r1) H[0].pop();
	if (y == r2) H[1].pop();
	size[y] = n;
	queue<int> Q;
	Q.push(y);
	while (!Q.empty())
	{
		int y = Q.front();
		Q.pop();
		int k = (n+d[y])%2;
		while (size[y] > 1)
		{
			if (H[k].empty())
			{
				puts("-1");
				return 0;
			}
			int x = H[k].top().Y;
			H[k].pop();
			addedge(x, y);
			size[x] = (n+d[y]-d[x])/2;
			size[y] -= size[x];
			Q.push(x);
		}
		if (size[y] != 1)
		{
			puts("-1");
			return 0;
		}
	}

	for (auto p : ans)
		printf("%d %d\n", p.X, p.Y);

	return 0;
}

