vector<pll> ans;


int main(void){
	int n;
	cin >> n;
	rep(i,n) cin >> d[i];

	rep(i,n) ary[d[i] % 2].push_back(pll(d[i],i));
	rep(i,2) sort(begin(ary[i]),end(ary[i]));

	if(ary[0].size() == n or ary[1].size() == n) NG();

	int tmp = (ary[1][0].first < ary[0][0].first);
	int pos[2] = {0,0};

	queue<int> que;
	const int root = ary[tmp][0].second;
	que.push(root);
	sz[root] = n;
	pos[tmp]++;


	while(!que.empty()){
		int cur = que.front(); que.pop();
		int csz = 1;

		if(csz == sz[cur]) continue;

		const int tar = (d[cur] + 1) % 2;
		while(pos[tar] < ary[tar].size()){
			const int nxt = ary[tar][pos[tar]].second;
			const ll add = (n - (d[nxt] - d[cur])) / 2LL;

			sz[nxt] = add,csz += add;
			graph[cur].push_back(nxt);
			graph[nxt].push_back(cur);
			ans.push_back(pll(cur + 1,nxt + 1));
			que.push(nxt);
			if(csz > sz[cur]) NG();
			pos[tar]++;
			if(csz == sz[cur]) break;
		}
	}

	const int alt = (root == 0);
	dfs(alt,-1,0);
	ll dsum = 0LL;
	rep(i,n) dsum += dist[i];
	if(dsum != d[alt]) NG();

	for(auto &elem:ans){
		cout << elem.first << " " << elem.second << endl;
	}


	return 0;
}

