	//	cout << c << " " << cn << endl;
		long long int a, b = 0;
		for (long long int i = c / (N - 1) + !!(c % (N - 1)); i <= sqrt(c); i++) {
			if (c%i)continue;
			a = i;
			b = c / i;
			long long int st = (b  - (a - 1));
			long long int add = st;
		//	cout << a << " " << b << " " << st << " " << add << endl;
			bool flag = true;
			while (st < c) {
				if (m.find(st) == m.end()) {
					flag = false;
					break;
				}
				add += 2;
				st += add;
			}
			if (flag) {
				st = (b - (a - 1));
				add = st;
				int bef = m[0];
				while (st <= c) {
			//		cout << st << endl;
					ans.push_back({ bef,m[st] });
					bef = m[st];
					m.erase(st);
					add += 2;
					st += add;
				}
			}
		}
	}
	for (auto i : ans) {
