vector<int> nei[100005];

int main()
{
	ios_base::sync_with_stdio(false);
	cin>>n;
	for(ll i=0;i<n;i++)
	{
		cin>>d[i];
		if(d[i]>n*(n-1)/2){
			cout<<-1;
			return 0;
		}
		m[d[i]]=i;
		p[i].first=d[i];
		p[i].second=i;
		sz[i]=1;
	}
	sort(p,p+n);
	reverse(p,p+n);
	memset(sub,-1,sizeof(sub));
	for(ll i=0;i<n-1;i++)
	{
		ll de=p[i].first;
		ll x=p[i].second;
		sub[x]=n-sz[x]*2;
		if(sub[x]==0){
			cout<<-1;
			return 0;	
		}
		if(m.find(de-sub[x])==m.end()){
			cout<<-1;
			return 0;
		}
		ll par=m[de-sub[x]];
		sz[par]+=sz[x];
		dp[par]+=dp[x]+sz[x];
		res.push_back(make_pair(x,par));
	}

	for(int i=0;i<n-1;i++){
		nei[res[i].first].push_back(res[i].second);
		nei[res[i].second].push_back(res[i].first);
	}

	queue<int> q;
	memset(dis,-1,sizeof(dis));
	q.push(p[n-1].second);
	dis[p[n-1].second]=0;

	while(!q.empty()){
		int last=q.front();
		q.pop();

		for(int to:nei[last]){
			if(dis[to]==-1){
				dis[to]=dis[last]+1;
				q.push(to);
			}
		}
	}

	ll sm=0;
	for(int i=0;i<n;i++){
		sm+=dis[i];
	}
	if(sm!=p[n-1].first){
		cout<<-1;
		return 0;
	}

	for(ll i=0;i<n-1;i++)
	{
		cout<<res[i].first+1<<" "<<res[i].second+1<<endl;
	}


	return 0;
}

