{
	vs[++ee].v=v;vs[ee].next=st[u];st[u]=ee;
}	
inline void dfs(int rt,int pr)
{
	if(vis[rt])
	{
		puts("-1"); exit(0);
	}
	vis[rt]=1;
	for(int i=st[rt];i;i=vs[i].next)
	{
		if(vs[i].v==pr) continue;
		dfs(vs[i].v,rt);
		sz[rt]+=sz[vs[i].v];
	}
}
