pair <long long,int> D[100050];

pair <int,int> edge[100050];

vector <int> G[100050];

int n,size[100050],tot;

long long dis[100050];

inline void dfs(int u,int fa)
{
	for (int i=0;i<G[u].size();i++)
	{
		int v=G[u][i];
		if (v!=fa)
		{
			dis[v]=dis[u]+1;
			dfs(v,u);
		}
	}
}

int main()
{
	n=read();
	for (int i=1;i<=n;i++)
	{
		int d=read();
		D[i]={d,i};
		size[i]=1;
	}
	sort(D+1,D+n+1);
	reverse(D+1,D+n+1);
	for (int i=1;i<n;i++)
	{
		long long delta=D[i].first-n+2*size[i];
		int l=1,r=n,pos=0; 
		while (l<=r)
		{
			int mid=(l+r)>>1;
			if (D[mid].first<=delta)
			{
				r=mid-1;
				pos=mid;
			}
			else
				l=mid+1;
		}
		//cout << D[pos].first << " " << delta << endl;
		if (D[pos].first!=delta)
		{
			puts("-1");
			return 0;
		}
		int u=D[i].second,v=D[pos].second;
		G[u].push_back(v);
		G[v].push_back(u);
		edge[++tot]={u,v};
		size[pos]+=size[i];
	}	
	dfs(D[n].second,0);
	long long sum=0;
	for (int i=1;i<=n;i++)
		sum+=dis[i];
	if (sum==D[n].first)
	{
		for (int i=1;i<=tot;i++)
			cout << edge[i].first << " " << edge[i].second << endl;
	}
	else
		puts("-1");
	return 0;
}

