int N;
LL D[100111];

VI G[100111];

int par[100111];
int sz[100111];
LL C[100111];

bool check() {
    VI ord;
    ord.reserve(N);
    ord.push_back(0);
    REP (i, N) {
	int v = ord[i];
	EACH (e, G[v]) if (*e != par[v]) {
	    ord.push_back(*e);
	    par[*e] = v;
	}
    }
    REP (i, N) {
	int v = ord[N-1-i];
	sz[v] = 1;
	EACH (e, G[v]) if (*e != par[v]) {
	    sz[v] += sz[*e];
	    C[v] += C[*e] + sz[*e];
	}
    }
    REP (i, N) {
	int v = ord[i];
	if (C[v] != D[v]) return false;
	EACH (e, G[v]) if (*e != par[v]) {
	    C[*e] += C[v] - (C[*e] + sz[*e]) + (N - sz[*e]);
	}
    }

    return true;
}

void MAIN() {
    scanf("%d", &N);
    REP (i, N) scanf("%lld", D+i);

    map<LL, pair<int, int> > mp;
    REP (i, N) mp[D[i]] = make_pair(i, 1);
    vector<pair<int, int> > E;
    E.reserve(N);
    bool yes = true;
    while (mp.size() > 1u) {
	auto z = prev(mp.end());
	int i = z->second.first;
	int sz = z->second.second;
	LL cst = D[i] + 2 * sz - N;
	if (cst == D[i]) {
	    yes = false;
	    break;
	}
	auto it = mp.find(cst);
	if (it == mp.end()) {
	    yes = false;
	    break;
	}

	G[i].push_back(it->second.first);
	G[it->second.first].push_back(i);
	E.emplace_back(i, it->second.first);
	it->second.second += sz;
	mp.erase(z);
    }

    if (yes && check()) {
	EACH (e, E) printf("%d %d\n", e->first+1, e->second+1);
    } else {
	puts("-1");
    }
}

int main() {
    int TC = 1;
//    scanf("%d", &TC);
    REP (tc, TC) MAIN();
    return 0;
}

