int n,nn;
struct node{int p,f,s;long long d,ss;};
node a[100010];

void qsort(int l,int r)
{
	int i,j;
	node m,t;
	i=l;j=r;
	m=a[(l+r)/2];
	while (i<=j)
	{
		while (a[i].d>m.d) i++;
		while (a[j].d<m.d) j--;
		if (i<=j)
		{
			t=a[i];a[i]=a[j];a[j]=t;
			i++;j--;
		}
	}
	if (l<j) qsort(l,j);
	if (i<r) qsort(i,r);
}

int find(long long x,int l,int r)
{
	int m=(l+r)/2;
	while (l<=r)
	{
		if (a[m].d==x) return m;
		if (a[m].d>x) l=m+1;
		else r=m-1;
		m=(l+r)/2;
	}
	return -1;
}

