		for (long long NP : V[P]) {
			if (Dists[NP] == -1) {
				Dists[NP] = Dists[P] + 1;
				Q.push(NP);
			}
		}
	}
	long long SUM = 0;
	for (int i = 0; i < N; i++) SUM += Dists[i];
	if (SUM != D[0].first) cout << -1 << endl;
	else for (int i = 0; i < N - 1; i++) cout << ANS[i].first + 1 << " " << ANS[i].second + 1 << endl;
}

