int siz[N],n,tot,vis[N];
map<LL,int>f;
struct Q{
	LL v;int id;
	bool operator<(const Q&x)const{
		return v>x.v;
	}
}q[N];
vector<int>g[N];
vector<pair<int,int> >ans;
bool flag=1;
void dfs(int x){
	tot++;vis[x]=1;
	for(auto v:g[x]){
		if(vis[v])flag=0;
		else dfs(v);
	}
}
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%lld",&q[i].v);
		f[q[i].v]=i;
		q[i].id=i;
		siz[i]=1;
	}
	sort(q+1,q+n+1);
	for(int i=1;i<n;i++){
		LL d=q[i].v+2*siz[q[i].id]-n;
		int fa=f[d];
		if(!fa)cout<<"-1"<<endl,exit(0);
		siz[fa]+=siz[q[i].id];
		ans.push_back(make_pair(q[i].id,fa));
		g[fa].push_back(q[i].id);
	}
	dfs(q[n].id);
	if(tot==n&&flag){
		for(auto x:ans){
			printf("%d %d\n",x.first,x.second);
		}
	}
	else puts("-1");
}

#include <cstdio>
#include <algorithm>
#include <cstring>
#include <map>
#define Maxn 1000008 
using namespace std;
typedef long long ll;

int n,cnt;
ll d;
int son[Maxn];
map <ll,int> M;

struct node{
	int num;
	ll val;
}N[Maxn];

bool cmp(node a,node b){	return a.val > b.val;}

int U[Maxn],V[Maxn];

int main()
{
	memset(son,0,sizeof(son));
	scanf("%d",&n);
	for(int i = 1;i <= n;i ++)
	{
		scanf("%lld",&d);
		M[d] = i;
		N[i].num = i,N[i].val = d;
	}
	sort(N+1,N+1+n,cmp);
	map<ll,int>::iterator iter;;
	for(int i = 1;i < n;i ++)
	{
		int u = N[i].num;
		if((N[i].val - n + 2LL + (son[u] << 1)) < N[n].val) continue;
		iter = M.find(N[i].val - n + 2 + (son[u] << 1));
		if(iter == M.end()) continue;
		int v = iter->second;
		son[v] += son[u]+1;
		U[cnt] = u,V[cnt++] = v;
	}
//	for(int i = 1;i <= n;i ++) printf("son[N[%d].num] = %d\n",i,son[N[i].num]);
	if(cnt != n - 1) printf("-1\n");
	else
		if(son[N[n].num] == n - 1) 
			for(int i = 0;i < cnt;i ++)
				printf("%d %d\n",U[i],V[i]);
		else printf("-1\n");
	return 0;
}

