int main() {
	int N; scanf("%d", &N);
	for (int i = 1; i <= N; i++) {
		ll x; scanf("%lld", &x);
		inp[i] = {x, i};
		siz[i] = 1;
	}
	sort(inp + 1, inp + N + 1);
	for (int i = N; i >= 2; i--) {
		int cur, id;
		tie(cur, id) = inp[i];
		int n = N - siz[id];
		ll del = n - siz[id];
		auto it = *lower_bound(inp + 1, inp + N + 1, pii(cur - del, 0));
		if (it.first != cur - del || !del) return !printf("-1");
		siz[it.second] += siz[id];
		ans.push_back({id, it.second});
	}
	for (pii x : ans) {
		printf("%lld %d\n", x.first, x.second);
	}
}

