void solve()
{
	ll N;
	cin >> N;
	vll D = cinv(N);
	vector<pair<ll, ll>> Dv;
	REP(v, N){
		Dv.emplace_back(D[v], v);
	}
	sort(D.begin(), D.end(), [](auto &a, auto &b){return a>b; });
	sort(Dv.begin(), Dv.end(),
		[](auto &a, auto &b){ return a>b; }
	);

	vll parent(N, -1);
	vll a(N, 1);
	vll dSum(N);

	REPS(i, 0, N-2){
		ll d, v;
		tie(d,v) = Dv[i];
		ll pd = d - (N-2*a[i]);
		//dpdi
		auto it = lower_bound(D.begin(), D.end(), pd,
			[](auto &a, auto &b){return a>b; }
		);
		if (it == D.end()) bye(-1);
		if (*it != pd) bye(-1);
		ll par_i = it - D.begin();
		parent[i] = Dv[par_i].second;
		a[par_i] += a[i];
		dSum[par_i] += dSum[i]+a[i];
	}

