struct UF{
    int n;
    vector<int> par,size;

    void init(int n_){
        n=n_;
        par.assign(n,-1);
        size.assign(n,1);

        for(int i=0;i<n;i++){
            par[i]=i;
        }
    }

    int root(int a){
        if(par[a]==a) return a;
        else return par[a]=root(par[a]);
    }

    void unite(int a,int b){
        if(root(a)!=root(b)){
            size[root(a)]+=size[root(b)];
            par[root(b)]=root(a);
        }
    }

    bool check(int a,int b){
        return root(a)==root(b);
    }
};

