map<ll, int> mp;
const int maxn = 1e5 + 5;
int sz[maxn];
ll d[maxn];
ll dp[maxn];
ll dp1[maxn];
vector<int> G[maxn];

void dfs(int u){
    sz[u] = 1;
    for(auto v : G[u]){
        dfs(v);
        sz[u] += sz[v];
        dp[u] += dp[v] + sz[v];
    }
}

void dfs1(int u){
    ll sum = 0;
    ll sum1 = 0;
    for(auto v : G[u]){
        sum += dp[v];
        sum1 += sz[v];
    }
    for(auto v : G[u]){
        dp1[v] = dp1[u] + sum - dp[v] + sum1 - sz[v] + n - sz[v];
    }
    for(auto v : G[u]){
        dfs1(v);
    }
}

bool check(int root){
    dfs(root);
    dfs1(root);
    for(int i = 1;i <= n;i++){
        if(dp[i] + dp1[i] != d[i])
            return false;
    }
    return true;
}

