    ios::sync_with_stdio(0), cin.tie(0), cout.tie(0);
    cin >> n;
    priority_queue<pi> pq;
    for(int i = 1;i <= n;i++){
        cin >> d[i];
        mp[d[i]] = i;
        pq.push({d[i], i});
        sz[i] = 1;
    }
    bool flag = true;
    int root = 0;
    while(!pq.empty()){
        pi p = pq.top(); pq.pop();
        ll dis = p.first;
        int id = p.second;
        if(sz[id] == n){
            root = id;
            break;
        }
        ll dis1 = dis - (n - 2 * sz[id]);
        if(mp.find(dis1) == mp.end()){
            flag = false;
            break;
        }else{
            int fa = mp[dis1];
            if(fa == id){
                flag = false;
                break;
            }
            G[fa].push_back(id);
            sz[fa] += sz[id];
        }
    }
    if(flag){
        flag &= check(root);
    }
    if(flag){
        for(int i = 1;i <= n;i++){
            for(auto v : G[i]){
                cout << i << " " << v << endl;
            }
        }
    }else{
        cout << -1 << endl;
    }
    return 0;
}

