    sz[u] = 1;
    for (int p = l[u]; p >= 0; p = E[p].x) {
        int v = E[p].v;
        dep[v] = dep[u] + 1;
        dfs1(v);
        sz[u] += sz[v];
    }
}

void dfs2(int u) {
    for (int p = l[u]; p >= 0; p = E[p].x) {
        int v = E[p].v;
        S[v] = S[u] + n - 2 * sz[v];
        dfs2(v);
    }
}

