ii dfs(int n) {
    int cnt = 0;
    ll res = 0;
    FORE (ch, children[n]) {
        ii r = dfs(ch);
        cnt += r.first;
        res += r.second;
    }
    res += cnt;
    return MP(cnt+1, res);
}

