#include<bits/stdc++.h>
using namespace std;

typedef long long ll;
const int MAXN = 100010;

struct Edge
{
	int to, nxt;
}e[MAXN << 1];
int tot, h[MAXN];

inline void add(int u, int v)
{
	e[++tot] = (Edge){v, h[u]};
	h[u] = tot;
}

struct Node
{
	int id; ll val;
	bool operator < (const Node &x) const
	{
		return val < x.val;
	}
}d[MAXN];

int n, vis[MAXN], siz[MAXN], fa[MAXN];
map<ll, int> H; ll sum;

bool solve(int x)
{
	if(vis[d[x].id]) return 1;
	vis[d[x].id] = 1;
	if(x == 1) return 1;
	ll now = d[x].val + 2ll * siz[d[x].id] - n;
	if(H.find(now) == H.end()) return 0;
	int f = H[now];
	if(!siz[d[f].id]) siz[d[f].id] = 1;
	siz[d[f].id] += siz[d[x].id];
	fa[d[x].id] = d[f].id;
	add(d[f].id, d[x].id), add(d[x].id, d[f].id);
	return solve(f);
}

void dfs(int x, int f, ll res)
{
	sum += res;
	for(int i = h[x]; i; i = e[i].nxt)
	{
		int y = e[i].to;
		if(y == f) continue;
		dfs(y, x, res + 1);
	}
}

int main()
{
	scanf("%d", &n);
	for(int i = 1; i <= n; ++i)
	{
		scanf("%lld", &d[i].val);
		d[i].id  = i;
	}
	sort(d + 1, d + 1 + n);
	for(int i = 1; i <= n; ++i) H[d[i].val] = i;
	for(int i = n; i > 1; --i)
		if(!vis[d[i].id])
		{
			siz[d[i].id] = 1;
			if(!solve(i)) return puts("-1"), 0;
		}
	dfs(d[1].id, 0, 0);
	if(sum != d[1].val) return puts("-1"), 0;
	for(int i = 2; i <= n; ++i) 
		printf("%d %d\n", fa[d[i].id], d[i].id);
	return 0;
}

