#define SZ(v) ((int)(v).size())  
#define FOR(i,a,b) for(int i=(a);i<(b);++i)  
#define REP(i,n) FOR(i,0,n)  
#define FORE(i,a,b) for(int i=(a);i<=(b);++i)  
#define REPE(i,n) FORE(i,0,n)  
#define FORSZ(i,a,v) FOR(i,a,SZ(v))  
#define REPSZ(i,v) REP(i,SZ(v))  
typedef long long ll;
typedef unsigned long long ull;
ll gcd(ll a, ll b) { return b == 0 ? a : gcd(b, a%b); }

const int MAXN = 100000;

int n;
ll d[MAXN];

pair<ll, int> o[MAXN];

int sz[MAXN], par[MAXN]; ll chdst[MAXN];


bool solve() {
	REP(i, n) o[i] = MP(d[i], i); sort(o, o + n);
	//printf("o:"); REP(i, n) printf(" (%lld,%d)", o[i].first, o[i].second); puts("");

	REP(i, n) sz[i] = 1, par[i] = -2, chdst[i] = 0;
	for (int i = n - 1; i >= 1; --i) {
		int at = o[i].second;
		// d[at]-d[par[at]]=n-2*sz[at] -> d[par[at]]=d[at]-n+2*sz[at]
		ll want = d[at] - n + 2 * sz[at];
		int idx = lower_bound(o, o + n, MP(want, -1)) - o; if (idx >= i || o[idx].first != want) return false;
		par[at] = o[idx].second; sz[par[at]] += sz[at]; chdst[par[at]] += chdst[at] + sz[at];
	}

	int root = o[0].second; assert(sz[root] == n); par[root] = -1; return chdst[root] == d[root];
}


