#define rep(X,A,B) for(int X=A;X<=B;X++)
#define tep(X,A,B) for(int X=A;X>=B;X--)
#define LL long long
#define DB double
const int N=400010;
const int M=800010;
const LL INF=1e15;
using namespace std;

int n,fa[N],sz[N];

struct nn{
	LL d;
	int x;
}a[N];

int cmp(nn A,nn B){
	return A.d<B.d;
}

void READ(){
	scanf("%d",&n);
	rep(i,1,n)scanf("%lld",&a[i].d),a[i].x=i;
	sort(a+1,a+n+1,cmp);
}

int GET(int pos,int x){
	int l=1,r=pos,res;
	while(l<=r){
		int mid=(l+r)>>1;
		if(a[mid].d<=x)res=mid,l=mid+1;
		else r=mid-1;
	}
	return res;
}

int SOLVE(){
	rep(i,1,n)sz[i]=1;
	tep(i,n,2){
		int x=a[i].x;
		if(sz[x]>n/2)return 0;
		LL df=a[i].d+2*sz[x]-n;
		int pos=GET(i-1,df);
		if(a[pos].d!=df)return 0;
		int pa;
		fa[x]=pa=a[pos].x;
		sz[pa]+=sz[x];
	}
	rep(i,2,n)printf("%d %d\n",fa[a[i].x],a[i].x);
	return 1;
}

int main(){
	READ();
	if(SOLVE()==0)printf("-1\n");
	return 0;
}

