    for (int i = 1; i <= n; i++) {
        v[i] = distances[i].first;
        bij[i] = distances[i].second;
    }

    if (!solve(n)) {
        cout << "-1\n";
        return 0;
    }

    for (int i = 2; i <= n; i++) {
        cout << bij[par[i]] << " " << bij[i] << '\n';
    }

