#include<cstdio>

int n,nn;
struct node{int p,f,l,r;long long d;};
node a[100010];

void qsort(int l,int r)
{
	int i,j;
	node m,t;
	i=l;j=r;
	m=a[(l+r)/2];
	while (i<=j)
	{
		while (a[i].d<m.d) i++;
		while (a[j].d>m.d) j--;
		if (i<=j)
		{
			t=a[i];a[i]=a[j];a[j]=t;
			i++;j--;
		}
	}
	if (l<j) qsort(l,j);
	if (i<r) qsort(i,r);
}

int main()
{
	scanf("%d",&n);
	nn=n-2;
	for (int i=1;i<=n;i++)
	{
		scanf("%d",&a[i].d);
		a[i].p=i;
		a[i].f=-1;
		a[i].l=-1;
		a[i].r=-1;
	}
	qsort(1,n);
	bool ok=true;
	a[1].l=0;a[1].r=n-1;
	for (int i=2;i<=n;i++)
	{
		bool check=true;
		for (int j=i-1;j>=1;j--)
		{
			if (a[i].d-a[j].d>nn) 
			{
				check=false;break;
			}
			else
			{
				if ((a[i].d-a[j].d-nn)%2==0)
				{
					int l,r;
					l=(nn+(a[i].d-a[j].d))/2;
					r=(nn-(a[i].d-a[j].d))/2;
					if (r<a[j].r)
					{
						a[i].f=a[j].p;
						a[i].l=nn-r;
						a[i].r=r;
						break;
					}
				}
			}
		}
		if (!check) {ok=false;break;}
	}
	if (ok)
	{
		for (int i=1;i<=n;i++)
		{
			if (a[i].f!=-1) printf("%d %d\n",a[i].f,a[i].p);
		}
	}
	else printf("-1");
}

