}
void dfs (int crr, int dpt, vector<vector<int>>& grh, long long& cnt) {
  cnt += dpt;
  for (int nxt : grh[crr]) {
    dfs(nxt, dpt + 1, grh, cnt);
  }
}
int main() {
  int n;
  cin >> n;
  vector<pair<long long, int>> di(n);
  for (int i = 0; i < n; i++) {
    int d;
    cin >> d;
    di[i] = {d, i};
  }
  sort(di.begin(), di.end());
  vector<long long> sz(n, 1);
  vector<int> prt(n);
  vector<vector<int>> grh(n);
  vector<pair<int, int>> edg;
  for (auto it = prev(di.end()); it != di.begin(); it--) {
    auto d = it->first;
    auto i = it->second;
    long long prtv = d - n + 2 * sz[i];
    auto jt = lower_bound(di.begin(), di.end(), make_pair(prtv, 0));
    if (jt->first != prtv) err();
    auto p = jt->second;
    prt[i] = p;
    grh[p].push_back(i);
    if (i == prt[i]) err();
    sz[prt[i]] += sz[i];
    edg.emplace_back(min(i, prt[i]), max(i, prt[i]));
  }
  long long cnt = 0;
  int rt = di[0].second;
  dfs(rt, 0, grh, cnt);
  if (di[0].first != cnt) err();
  sort(edg.begin(), edg.end());
  for (auto const & e : edg) {
    cout << e.first + 1 << ' ' << e.second + 1 << endl;
  }
  return 0;
}

