const int maxn=1e5+10;
struct edge{int v,nxt;}e[maxn<<1];
int head[maxn],ecnt=0;
void add(int u,int v)
{
	e[++ecnt]=(edge){v,head[u]},head[u]=ecnt;
	e[++ecnt]=(edge){u,head[v]},head[v]=ecnt;
}
struct data
{
	long long D;
	int id;
	bool operator < (const data &b){return D<b.D;}
}c[maxn];
int fa[maxn],siz[maxn],n,rt;
int find(int lim,long long x)
{
	int l=1,r=lim,ans=0;
	while(l<=r)
	{
		int mid=l+r>>1;
		if(c[mid].D>=x)r=mid-1,ans=mid;
		else l=mid+1;
	}
//	cout<<c[ans].D<<endl;
//	if(c[ans].D!=x)printf("fuckyou\n");
	return ans;
}
int dep[maxn];
long long tot=0;
void dfs(int u,int pre)
{
	dep[u]=dep[pre]+1;
	tot+=dep[u]-1;
	for(int i=head[u];~i;i=e[i].nxt)
	{
		int v=e[i].v;
		if(v==pre)continue;
		dfs(v,u);
	}
}
int main()
{
	memset(head,-1,sizeof(head));
	scanf("%d",&n);
	for(int i=1;i<=n;i++)scanf("%lld",&c[i].D),c[i].id=i;
	sort(c+1,c+1+n);
	for(int i=n;i>=2;i--)
	{
		siz[i]++;
		int p=find(i-1,c[i].D-(n-2*1LL*siz[i]));
		if(c[i].D-(n-2*1LL*siz[i])!=c[p].D)
		{
			printf("-1\n");
			return 0;
		}
		siz[p]+=siz[i];
		fa[c[i].id]=c[p].id;
	}
	for(int i=1;i<=n;i++)
	if(fa[i])add(fa[i],i);
	else rt=i;
	dfs(rt,0);
	if(tot!=c[1].D)
	{
		printf("-1\n");
		return 0;
	}
	for(int i=1;i<=n;i++)if(fa[i])printf("%d %d\n",fa[i],i);
}

