const int Maxn = 100005;
int n, cnt, root, fa[Maxn], siz[Maxn], pos[Maxn], head[Maxn];
long long d[Maxn], dis[Maxn];
map <long long, int> Ma;
struct edg
{
	int nxt, to;
}edge[Maxn];
void add(int x, int y)
{
	edge[++cnt] = (edg){head[x], y};
	head[x] = cnt;
}
void dfs1(int u)
{
	for (int i = head[u]; i; i = edge[i].nxt)
	{
		int to = edge[i].to;
		dfs1(to), dis[u] += dis[to] + siz[to];
	}
}
void dfs2(int u)
{
	for (int i = head[u]; i; i = edge[i].nxt)
	{
		int to = edge[i].to;
		dis[to] = dis[u] + n - 2 * siz[to];
		dfs2(to);
	}
}
int main()
{
	scanf("%d", &n);
	root = 1;
	for (int i = 1; i <= n; i++)
		scanf("%lld", &d[i]), pos[i] = i, Ma[d[i]] = i, siz[i] = 1;
	sort(pos + 1, pos + 1 + n, [](int x, int y){return d[x] < d[y];});
	for (int i = n; i >= 1; i--)
	{
		Ma[d[pos[i]]] = 0;
		fa[pos[i]] = Ma[d[pos[i]] - n + 2 * siz[pos[i]]];
		siz[fa[pos[i]]] += siz[pos[i]];
	}
	for (int i = 1; i <= n; i++)
	{
		if (!fa[i] && i != pos[1])
		{
			puts("-1");
			return 0;
		}
		add(fa[i], i);
	}
	dfs1(pos[1]);
	for (int i = 2; i <= n; i++)
		dis[pos[i]] = 0;
	dfs2(pos[1]);
	for (int i = 1; i <= n; i++)
		if (d[i] != dis[i])
		{
			puts("-1");
			return 0;
		}
	for (int i = 2; i <= n; i++)
		printf("%d %d\n", fa[pos[i]], pos[i]);
	return 0;
}

