LL N;
PP Q[100000];
LL A[100000];
LL P[100000];
LL cnt[100000];
map<LL, int> mp;

bool cat(int from) {
	LL t = A[from] - (N - 2 * cnt[from]);
	if (!mp.count(t)) return false;
	if (t >= A[from]) return false;
	int to = mp[t];
	P[from] = to;
	cnt[to] += cnt[from];
	return true;
}

void sub() {
	Fill(cnt, 1);
	REP(i, 0, N) {
		A[i] = Q[i].first;
		mp[A[i]] = i;
	}

	REM(i, 1, N) {
		if (!cat(i)) {
			cout << -1 << endl;
			return;
		}
	}

	REP(i, 1, N) {
		cout << Q[i].second + 1 << ' ' << Q[P[i]].second + 1 << endl;
	}
}

int main() {
	cin >> N;
	REP(i, 0, N) {
		LL a;
		cin >> a;
		Q[i] = PP(a, i);
	}
	sort(Q, Q + N);

	sub();
	return 0;
}

