const int MAXN = 1e5 + 10;

int n;
ll D[MAXN];

bool read() {
    if (!(cin >> n)) {
        return 0;
    }
    forn(i, n) {
        cin >> D[i];
    }
    return 1;
}

int sz[MAXN];
vi g[MAXN];

void dfs(int v, int de, ll& sum) {
    sum += de;
    for (int to : g[v]) {
        dfs(to, de + 1, sum);
    }
}

bool solve() {
    forn(i, n) {
        g[i].clear();
        sz[i] = 1;
    }

    vi perm(n);
    iota(all(perm), 0);
    sort(all(perm), [&](int v, int u) { return D[v] > D[u]; });

    map<ll, int> pos_D;
    forn(v, n) {
        pos_D[D[v]] = v;
    }

    forn(it, n - 1) {
        const int v = perm[it];
        ll Dp = D[v] - n + 2 * sz[v];
        if (!pos_D.count(Dp)) {
//            debug(v + 1, sz[v], D[v], Dp);
            return 0;
        }
        int p = pos_D.at(Dp);
        g[p].pb(v);
        sz[p] += sz[v];
//        debug(p + 1, v + 1);
    }

    const int root = perm.back();
    ll sum = 0;
    dfs(root, 0, sum);
    if (sum != D[root]) {
//        debug(sum, D[root]);
        return 0;
    }
    ford(it, n) {
        const int v = perm[it];
        for (int to : g[v]) {
            cout << v + 1 << " " << to + 1 << "\n";
        }
    }
    return 1;
}

