vector<vector<int>> g;

long calc(int i, long s, int p) {
    long res = s;
    for (int x : g[i]) {
        if (x != p)
        res += calc(x, s + 1, i);
    }
    return res;
}

int main() {
    std::ios::sync_with_stdio(false);

    int n;
    cin >> n;
    vector<pair<long, int>> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i].first;
        a[i].second = i;
    }

    std::sort(a.rbegin(), a.rend());

    map<long, int> q;
    for (int i = 0; i < n; i++) {
        q[a[i].first] = i;
    }

    vector<int> s(n);

    g.resize(n);

    for (int i = 0; i < n - 1; i++) {
        s[i]++;
        long p = a[i].first - (n - 2 * s[i]);
        if (p >= a[i].first) {
            cout << -1;
            return 0;
        }
        if (q.find(p) == q.end()) {
            cout << -1;
            return 0;
        }
        int x = q[p];
        s[x] += s[i];
        g[x].push_back(i);
        g[i].push_back(x);
    }

    if (calc(n - 1, 0, -1) != a[n - 1].first) {
        cout << -1;
        return 0;
    }

