struct edge{
	int to,nxt;
}e[maxn<<1];

struct node{
	ll dis;int id;
	bool operator < (const node &A) const {return dis>A.dis;}
}a[maxn];

struct uv{
	int u,v;
	bool operator < (const uv &A) const {return u==A.u?v<A.v:u<A.u;}
}ans[maxn];

int cnt,head[maxn],n;ll dis[maxn],size[maxn];

void add(int u,int v){
	e[++cnt]=(edge){v,head[u]};
	head[u]=cnt;
}

void dfs(int u,int fa,int dep){
	dis[a[n].id]+=dep;size[u]=1;
	for(int i=head[u];i;i=e[i].nxt){
		int v=e[i].to;
		if(v==fa) continue;
		dfs(v,u,dep+1);
		size[u]+=size[v];
	}
}

void dfs(int u,int fa){
	for(int i=head[u];i;i=e[i].nxt){
		int v=e[i].to;
		if(v==fa) continue;
		dis[v]=dis[u]+n-size[v]*2;
		dfs(v,u);
	}
}

