	for(int i=1;i<n;i++){
		int u=a[i].id;
		ll tmp=n-size[u]*2;
		if(tmp<=0||!mp.count(a[i].dis-tmp)) error
		int fa=mp[a[i].dis-tmp];
		size[fa]+=size[u];
		add(u,fa);add(fa,u);
		ans[i].u=min(u,fa);
		ans[i].v=max(u,fa);
	}
	sort(ans+1,ans+n);
	dfs(a[n].id,0,0);
	dfs(a[n].id,0);
	for(int i=1;i<=n;i++) if(dis[a[i].id]!=a[i].dis) error
	for(int i=1;i<n;i++) printf("%d %d\n",ans[i].u,ans[i].v);
}

