int main() {
  int n;
  cin >> n;
  vector<long long> d(n);
  map<long long, int> mp;
  for (int i = 0; i < n; i++) {
    cin >> d[i];
    mp[d[i]] = i;
  }
  sort(d.begin(), d.end(), greater<long long>());
  vector<long long> sz(n, 1);
  vector<vector<int>> grh(n);
  for (auto it = d.begin(); next(it) != d.end(); it++) {
    auto i = mp[*it];
    long long ptv = *it - n + 2 * sz[i];
    auto jt = lower_bound(d.rbegin(), d.rend(), ptv);
    if (jt == d.rend() || *jt != ptv) err();
    auto p = mp[*jt];
    grh[p].push_back(i);
    sz[p] += sz[i];
  }
  long long cnt = 0;
  int rt = mp[d[n - 1]];
  dfs(rt, 0, grh, cnt);
  if (d[n - 1] != cnt) err();
  for (int crr = 0; crr < n; crr++) {
    for (int nxt : grh[crr]) {
      cout << crr + 1 << ' ' << nxt + 1 << endl;
