int main() {
  int n;
  scan(n);
  vvll d(n, vll(2));
  map<ll, int> mp;
  rep (i, n) {
    scanf("%lld", &d[i][0]);
    d[i][1] = i;
    mp[d[i][0]] = i;
  }
  sort(all(d));
  reverse(all(d));
  vi parent(n, -1);
  vvi childs(n);
  vi allcnt(n, 0); // sum cnt of all childs
  vvi edges;
  vll accum_d(n, 0);
  rep (i, n-1) {
    int ind = d[i][1];
    allcnt[ind] = 1;
    for (auto c : childs[ind]) {
      allcnt[ind] += allcnt[c];
      accum_d[ind] += (accum_d[c] + allcnt[c]);
    }
    int diff = n - allcnt[ind] * 2;
    if (diff <= 0) {
      printf("-1\n");
      return 0;
    }
    ll next_d = d[i][0] - diff;
    if (mp.find(next_d) != mp.end()) {
      int par_ind = mp[next_d];
      parent[ind] = par_ind;
      childs[par_ind].push_back(ind);
      edges.push_back((vi){ind, par_ind});
    } else {
      printf("-1\n");
      return 0;
    }
  }
  {
    int ind = d[n-1][1];
    for (auto c : childs[ind]) {
      accum_d[ind] += (accum_d[c] + allcnt[c]);
    }
    if (accum_d[ind] != d[n-1][0]) {
      printf("-1\n");
      return 0;
    }
  }
  assert((int)edges.size() == n-1);
  rep (i, n-1) {
    printf("%d %d\n", edges[i][0]+1, edges[i][1]+1);
  }

  return 0;
}

