using namespace std;

int main() {
	auto no = [](){ printf("-1\n"); exit(0); };
	int n;
	scanf("%d", &n);
	vector<long long> d(n);
	map<long long, int> mp;
	rep(i, n) {
		scanf("%lld", &d[i]);
		mp[d[i]] = i;
	}
	sort(d.begin(), d.end());
	long long mi = d[0];
	reverse(d.begin(), d.end());
	d.pop_back();
	set<long long> remains;
	rep(i, d.size()) {
		remains.insert(d[i]);
	}
	vector<pair<int, int>> ans;
	rep(i, d.size()) {
		if (remains.count(d[i]) == 0) continue;
		remains.erase(d[i]);
		int s = 1;
		long long cur = d[i];
		long long prev;
		while (true) {
			long long diff = n - s * 2;
			prev = cur;
			cur -= diff;
			if (cur == mi) {
				ans.push_back({mp[prev], mp[cur]});
				break;
			}
			if (remains.count(cur) == 0) no();
			remains.erase(cur);
			ans.push_back({mp[prev], mp[cur]});
			s ++;
		}
	}
	assert(ans.size() == n - 1);
	for (auto it : ans) printf("%d %d\n", it.first + 1, it.second + 1);
}

