int main() {
    int N; cin >> N;
    if (N == 2) fail();
    map<Int, pair<int, int>> M; // D[i] -> idx, num
    vector<pair<int, int>> ans;
    for (int i = 0; i < N; i++) {
        Int d; cin >> d;
        M[d] = make_pair(i, 1);
    }
    for (auto it = rbegin(M), ite = rend(M); it != ite; ++it) {
        int idx, num; tie(idx, num) = it->second;
        if (num == N) continue;
        Int diff = num - (N - num);
        if (diff >= 0) fail();
        Int dnext = it->first + diff;
        if (!M.count(dnext)) fail();
        ans.emplace_back(idx, M[dnext].first);
        M[dnext].second += num;
    }
    for (auto &p : ans) {
        cout << p.first + 1 << ' ' << p.second + 1 << '\n';
    }
    return 0;
}

