const int MaxN = 1e5 + 5;  

int n; 
int sze[MaxN]; 
bool vis[MaxN]; 

s64 d[MaxN], c[MaxN];
std::vector<pii> ans; 
std::map<int, int> pos; 

int fa[MaxN]; 
std::vector<int> adj[MaxN]; 

inline void addEdge(int u, int v) {
	sze[u] += sze[v]; 
	adj[u].push_back(v); 
	ans.push_back(pii(u, v)); 
}

inline void dfs(int u) {
	for (int v : adj[u]) {
		dfs(v); 
		c[u] += c[v] + sze[v]; 
	}
}

bool check() {
	int G = pos[d[n]]; 
	return dfs(G), c[G] == d[n]; 
}

int main() {
